mapper.select:
		select admin_id, admin_name, admin_pwd, is_delete
		from admin
         where admin_name = #{adminName}
         and admin_pwd = #{adminPwd}
         and is_delete = #{isDelete}

mapper.update:
update admininfo set 
admin_name = #{adminName}, admin_pwd = #{adminPwd},
where admin_id = #{adminId}
