mapper.select:
		select user_id, user_name, user_pwd, user_email, user_phone, user_date, is_delete
		from userinfo
         where (user_name = #{userName} or user_phone = #{userName} or user_email = #{userName})
         and user_pwd = #{userPwd}
         and is_delete = #{isDelete}

select user_id,user_name,user_pwd,user_phone,user_email,user_date from userinfo where 1=1 

	and user_id = #{userId}


	and userName = #{userName}


	and user_phone = #{userPhone}


	and user_email = #{userEmail}

mapper.update:
update userinfo set 
user_name = #{userName}, user_pwd = #{userPwd},
user_email = #{userEmail}, user_phone = #{userPhone},user_date = #{userDate}
where user_id = #{userId}
